    <script src="js/jquery.js"></script>
    <script type="text/javascript">

      /* The following array controls the entire pagination*/

      var dates= Array("#25th","#26th","#27th","#28th","#29th");
      var curr_page = 0

      $("#nextPage").click(function(){
        $("#prevPage").removeClass("hidden");

        $(dates[curr_page]).removeClass("shown");
        $(dates[curr_page]).addClass("hidden");

        curr_page = (curr_page+1);
        $("title").text("Events and Activities on "+(curr_page+25)+"th");
        $(dates[curr_page]).removeClass("hidden");
        $(dates[curr_page]).addClass("shown");

        if (curr_page == (dates.length-1)) {

          $("#nextPage").addClass("hidden");
        }
      });

      $("#prevPage").click(function(){

        $("#nextPage").removeClass("hidden");

        $(dates[curr_page]).removeClass("shown");
        $(dates[curr_page]).addClass("hidden");

        curr_page = (curr_page-1);
        $("title").text("Events and Activities on "+(curr_page+25)+"th");
        $(dates[curr_page]).removeClass("hidden");
        $(dates[curr_page]).addClass("shown");

        if (curr_page == 0) {
          $("#prevPage").addClass("hidden");
        }
      });
    </script>

    <script src="js/bootstrap.min.js"></script>
  </body>

